/** @odoo-module **/

import { Define } from '@mail/define';

export default Define`
    {Test}
        [Test/name]
            openChat: open new chat for user
        [Test/model]
            Env
        [Test/assertions]
            3
        [Test/scenario]
            :testEnv
                {Record/insert}
                    [Record/traits]
                        Env
            @testEnv
            .{Record/insert}
                []
                    [Record/traits]
                        res.partner
                    [res.partner/id]
                        14
                []
                    [Record/traits]
                        res.users
                    [res.users/id]
                        11
                    [res.users/partner_id]
                        14
            @testEnv
            .{Record/insert}
                [Record/traits]
                    Server
                [Server/data]
                    @record
                    .{Test/data}
            :existingChat
                @testEnv
                .{Record/find}
                    [Record/traits]
                        Thread
                    {func}
                        [in]
                            thread
                        [out]
                            @thread
                            .{Thread/channelType}
                            .{=}
                                chat
                            .{&}
                                @thread
                                .{Thread/correspondent}
                            .{&}
                                @thread
                                .{Thread/correspondent}
                                .{Partner/id}
                                .{=}
                                    14
                            .{&}
                                @thread
                                .{Thread/model}
                                .{=}
                                    mail.channel
                            .{&}
                                @thread
                                .{Thread/public}
                                .{=}
                                    private
            {Test/assert}
                [0]
                    @record
                [1]
                    @existingChat
                    .{isFalsy}
                [2]
                    a chat should not exist with the target partner initially

            @testEnv
            .{Env/openChat}
                [partnerId]
                    14
            :chat
                @testEnv
                .{Record/find}
                    [Record/traits]
                        Thread
                    {func}
                        [in]
                            thread
                        [out]
                            @thread
                            .{Thread/channelType}
                            .{=}
                                chat
                            .{&}
                                @thread
                                .{Thread/correspondent}
                            .{&}
                                @thread
                                .{Thread/correspondent}
                                .{Partner/id}
                                .{=}
                                    14
                            .{&}
                                @thread
                                .{Thread/model}
                                .{=}
                                    mail.channel
                            .{&}
                                @thread
                                .{Thread/public}
                                .{=}
                                    private
            {Test/assert}
                [0]
                    @record
                [1]
                    @chat
                [2]
                    a chat should exist with the target partner
            {Test/assert}
                [0]
                    @record
                [1]
                    @chat
                    .{Thread/threadViews}
                    .{Collection/length}
                    .{=}
                        1
                [2]
                    the chat should be displayed in a 'ThreadView'
`;
